    public static ArrayList<Wisata> getListData(){
        ArrayList<Wisata> listWisata = new ArrayList<>();
        for (String[] aData : data){
            Wisata wisata = new Wisata();
            wisata.setNama_wisata(aData[0]);
            wisata.setKeterangan(aData[1]);
            wisata.setLokasi(aData[2]);
            wisata.setImage_wisata(aData[3]);

            listWisata.add(wisata);
        }
    return listWisata;
    }
